describe("isState filter",function(){beforeEach(module("ui.router")),beforeEach(module(function(e){e.
state("a",{url:"/"}).state("a.b",{url:"/b"})})),it("should return true if the current state exactly matches the input state"
,inject(function(e,t,n,r){t.go("a"),n.flush(),expect(e('"a" | isState')(r)).toBe(!0)})),it("should return false if the current state does not exactly match the input state"
,inject(function(e,t,n,r){n.go("a.b"),t.flush(),expect(e('"a" | isState')(r)).toBe(!1)}))}),describe("includedByState filter"
,function(){beforeEach(module("ui.router")),beforeEach(module(function(e){e.state("a",{url:"/"}).state
("a.b",{url:"/b"}).state("c",{url:"/c"})})),it("should return true if the current state exactly matches the input state"
,inject(function(e,t,n,r){t.go("a"),n.flush(),expect(e('"a" | includedByState')(r)).toBe(!0)})),it("should return true if the current state includes the input state"
,inject(function(e,t,n,r){t.go("a.b"),n.flush(),expect(e('"a" | includedByState')(r)).toBe(!0)})),it("should return false if the current state does not include input state"
,inject(function(e,t,n,r){t.go("c"),n.flush(),expect(e('"a" | includedByState')(r)).toBe(!1)}))});